import React, { Component, Fragment, useState } from "react";
import { getAuth, signInWithEmailAndPassword } from 'firebase/auth';
import { app } from "../fireBase";
const auth = getAuth(app);

const SigninPage = () => {
    const [email, setEmail] = useState("");
    const [Password, setPassword] = useState("");

    const signinUser = () => {
        signInWithEmailAndPassword(auth, email, Password).then(value => console.log("Signin success"))
            .catch((err) => console.log(err));
    }
}

class Signin extends Component {

    render() {

        return (
            <Fragment>
                <div className="signin-page">
                    <h1>Signin Page</h1>
                    <label>Enter you Email</label>
                    <input
                        onChange={(e) => setEmail(e.target.value)}
                        type="email"
                        required placeholder="enter you email here" /><br></br>
                    <label>Password</label>
                    <input
                        onChange={(e) => setEmail(e.target.value)}
                        type="Password" required placeholder="enter you Password here" /><br></br>
                    <button >Signin</button>
                </div>
            </Fragment>
        )
    }
}
export default Signin
